import React, { useState, useEffect, useRef } from 'react';
import useWebSocket from './useWebSocket';
import './App.css';

const WEBSOCKET_URL = 'ws://localhost:8080';

function App() {
  const { isConnected, sendMessage } = useWebSocket(WEBSOCKET_URL);
  const [animationState, setAnimationState] = useState('idle'); // 'idle', 'running', 'stopping'
  const animationTimeoutRef = useRef(null); // Ref to hold the timeout ID

  useEffect(() => {
    // Cleanup timeout if component unmounts or animationState changes
    return () => {
      if (animationTimeoutRef.current) {
        clearTimeout(animationTimeoutRef.current);
      }
    };
  }, []);

  const handleStartAnimation = () => {
    if (isConnected) {
      sendMessage({ type: 'START_ANIMATION' });
      setAnimationState('running');
      // The animation itself will be driven by CSS keyframes
      // We don't need continuous data from the backend for this specific visual.
    } else {
      alert('Not connected to WebSocket server.');
    }
  };

  const handleStopAnimation = () => {
    if (isConnected) {
      sendMessage({ type: 'STOP_ANIMATION' });
      setAnimationState('stopping'); // Trigger graceful stop if CSS allows
      // For immediate stop, you might set animationState to 'idle' directly
      // setAnimationState('idle');
    }
  };

  return (
    <div className="App">
      <div className="animation-container">
        {/* The main gradient bar element */}
        <div className={`gradient-bar ${animationState}`}></div>

        {/* Text appears when animation is running or done its first loop */}
        <h2 className={`animation-text ${animationState === 'running' ? 'show-text' : ''}`}>
          Apple Intelligence
        </h2>
      </div>

      <div className="controls">
        <button onClick={handleStartAnimation} disabled={!isConnected || animationState === 'running'}>
          Start Animation
        </button>
        <button onClick={handleStopAnimation} disabled={!isConnected || animationState === 'idle'}>
          Stop Animation
        </button>
      </div>

      <p className="connection-status">Connection Status: {isConnected ? 'Connected' : 'Disconnected'}</p>
    </div>
  );
}

export default App;
