import React, { useRef, useState, useEffect } from 'react';

const App = () => {
  const gradientBarRef = useRef(null);
  const [animationActive, setAnimationActive] = useState(false);
  const [position, setPosition] = useState({ x: 0, y: 50 }); // Initial position

  // This effect controls the animation loop
  useEffect(() => {
    let animationFrameId;
    let angle = 0; // To control the wave effect

    const animate = () => {
      if (!animationActive) {
        // If animation is not active, stop the loop
        cancelAnimationFrame(animationFrameId);
        return;
      }

      // Calculate new x and y positions
      const newX = (angle % 360) / 3.6; // 0% to 100%
      const newY = 50 + 10 * Math.sin((angle * Math.PI) / 180); // Wave on y-axis, reduced amplitude for subtlety

      setPosition({ x: newX, y: newY });
      angle += 1; // Increment angle for smoother, slower movement than the server example

      animationFrameId = requestAnimationFrame(animate);
    };

    if (animationActive) {
      animationFrameId = requestAnimationFrame(animate);
    }

    // Cleanup function to stop animation when component unmounts or animationActive becomes false
    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [animationActive]); // Re-run effect when animationActive changes

  const handleStart = () => {
    setAnimationActive(true);
  };

  const handleStop = () => {
    setAnimationActive(false);
  };

  useEffect(() => {
    if (gradientBarRef.current) {
      gradientBarRef.current.style.left = `${position.x}%`;
      gradientBarRef.current.style.top = `${position.y}%`;
    }
  }, [position]); // Update style whenever position changes

  return (
    <div style={{ padding: '2rem' }}>
      <h1>‚ú® Local Gradient Animation</h1>
      <button onClick={handleStart} style={{ marginRight: '10px' }}>
        Start Animation
      </button>
      <button onClick={handleStop}>Stop Animation</button>

      <div
        style={{
          position: 'relative',
          width: '100%',
          height: '200px', // Adjusted height to make the wave more noticeable in a smaller container
          border: '2px solid black',
          marginTop: '2rem',
          overflow: 'hidden',
          backgroundColor: '#ffffff',
        }}
      >
        <div
          ref={gradientBarRef}
          style={{
            position: 'absolute',
            width: '200px',
            height: '20px',
            background: 'linear-gradient(to right, #0af, #a0f, #f06, #f90)',
            borderRadius: '10px',
            filter: 'blur(15px)', // Slightly less blur
            transition: 'top 0.05s linear, left 0.05s linear', // Faster transition for smoother movement
          }}
        ></div>
      </div>

      <div style={{ marginTop: '1rem' }}>
        <p>üìç X: {position.x.toFixed(2)}%, Y: {position.y.toFixed(2)}%</p>
      </div>
    </div>
  );
};

export default App;
