import React, { useState, useRef, useEffect } from "react";
import "./App.css";

export default function App() {
  const [state, setState] = useState("idle");
  const barRef = useRef(null);

  // Cleanup-based listener for when 'completed' animation finishes
  useEffect(() => {
    const node = barRef.current;
    if (!node) return;

    const handleAnimationEnd = (e) => {
      if (state === "completed") {
        setState("idle");
      }
    };

    node.addEventListener("animationend", handleAnimationEnd);

    return () => {
      node.removeEventListener("animationend", handleAnimationEnd);
    };
  }, [state]);

  // Starts the animation sequence
  const start = () => {
    setState("idle");

    setTimeout(() => {
      setState("expanding");

      setTimeout(() => {
        setState("shrinking");

        setTimeout(() => {
          setState("completed");
        }, 1000); // shrinking duration
      }, 2000); // expanding duration
    }, 100); // slight reset delay
  };

  // Stops the animation and resets
  const stop = () => {
    setState("idle");
  };

  const isAnimating = ["expanding", "shrinking", "completed"].includes(state);

  return (
    <div className="app">
      <div className="animation-container">
        <div className={`gradient-bar-container ${state}`}>
          <div ref={barRef} className={`gradient-bar ${state}`} />
        </div>

        <h2 className={`text-title ${isAnimating ? "lift" : ""}`}>
          Apple Intelligence
        </h2>
      </div>

      <div className="button-container">
        <button
          onClick={start}
          disabled={isAnimating}
          className="start-button"
        >
          Start Animation
        </button>

        <button
          onClick={stop}
          disabled={state === "idle"}
          className="stop-button"
        >
          Stop Animation
        </button>
      </div>

      <p className="connection-status">
        Connection Status: ðŸŸ¢ Connected
      </p>
    </div>
  );
}
