import React, { useState, useEffect, useRef } from "react";
import "./App.css"; // Import the external CSS

function App() {
  const [animationState, setAnimationState] = useState("idle");
  const [showTextGradient, setShowTextGradient] = useState(false);
  const [isConnected] = useState(true);

  const animationTimeoutRef = useRef(null);
  const shrinkingTimeoutRef = useRef(null);
  const textGradientTimeoutRef = useRef(null);

  useEffect(() => {
    return () => {
      clearTimeout(animationTimeoutRef.current);
      clearTimeout(shrinkingTimeoutRef.current);
      clearTimeout(textGradientTimeoutRef.current);
    };
  }, []);

  const clearAllTimeouts = () => {
    clearTimeout(animationTimeoutRef.current);
    clearTimeout(shrinkingTimeoutRef.current);
    clearTimeout(textGradientTimeoutRef.current);
  };

  const handleStartAnimation = () => {
    clearAllTimeouts();
    setAnimationState("idle");
    setShowTextGradient(false);

    setTimeout(() => {
      setAnimationState("expanding");

      animationTimeoutRef.current = setTimeout(() => {
        setAnimationState("shrinking");

        shrinkingTimeoutRef.current = setTimeout(() => {
          setAnimationState("completed");
        }, 1000);
      }, 2000);
    }, 100);
  };

  const handleStopAnimation = () => {
    clearAllTimeouts();
    setAnimationState("idle");
    setShowTextGradient(false);
  };

const handleAnimationEnd = () => {};
// ðŸ‘‡ Add this just after the first useEffect (the cleanup one)
useEffect(() => {
  if (animationState === 'completed') {
    const delay = setTimeout(() => {
      setShowTextGradient(true); // this triggers the title lift
      setAnimationState('idle'); // optional: reset to idle after it lifts
    }, 300); // 300ms delay after bar is completed

    return () => clearTimeout(delay);
  }
}, [animationState]);


  const isAnimating =
    animationState === "expanding" ||
    animationState === "shrinking" ||
    animationState === "completed";

  return (
    <div className="app">
      <div className="animation-container">
        <div className={`gradient-bar-container ${animationState}`}>
          <div
            className={`gradient-bar ${animationState}`}
            onAnimationEnd={handleAnimationEnd}
          />
        </div>

        <h2
          className={`text-title ${isAnimating ? "lift" : ""} ${
            showTextGradient ? "gradient-active" : ""
          }`}
        >
          Apple Intelligence
        </h2>
      </div>

      <div className="button-container">
        <button
          onClick={handleStartAnimation}
          disabled={!isConnected || isAnimating}
          className="start-button"
        >
          Start Animation
        </button>

        <button
          onClick={handleStopAnimation}
          disabled={!isConnected || animationState === "idle"}
          className="stop-button"
        >
          Stop Animation
        </button>
      </div>

      <p className="connection-status">
        Connection Status: {isConnected ? "ðŸŸ¢ Connected" : "ðŸ”´ Disconnected"}
      </p>
    </div>
  );
}

export default App;
