import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function App() {
  const [animationState, setAnimationState] = useState("idle");
  const [isConnected] = useState(true);
  const animationTimeoutRef   = useRef(null);
  const completionTimeoutRef  = useRef(null);

  /* ‚Ä¶ your existing useEffect & handlers stay unchanged ‚Ä¶ */

  // üëâ NEW: quick helper
  const isAnimating = animationState === "expanding" || animationState === "shrinking";

  return (
    <div className="app">
      <div className="animation-container">
        <div className={`gradient-bar-container ${animationState}`}>
          <div className={`gradient-bar ${animationState}`} />
        </div>

        {/* üëâ Add the ‚Äúlift‚Äù class only while animating */}
        <h2 className={`text-title ${isAnimating ? "lift" : ""}`}>
          Apple Intelligence
        </h2>
      </div>

      {/* ‚Ä¶ buttons + status unchanged ‚Ä¶ */}
    </div>
  );
}

export default App;
