import React, { useRef, useState, useEffect } from 'react';

const App = () => {
  const [data, setData] = useState(null);
  const socketRef = useRef(null);
  const [connected, setConnected] = useState(false);
  const dotRef = useRef(null);

  useEffect(() => {
    socketRef.current = new WebSocket('ws://localhost:8080');

    socketRef.current.onopen = () => {
      console.log('‚úÖ Connected to WebSocket server');
      setConnected(true);
    };

    socketRef.current.onmessage = (event) => {
      const frame = JSON.parse(event.data);
      setData(frame);

      if (dotRef.current) {
        dotRef.current.style.left = `${frame.x}%`;
        dotRef.current.style.top = `${frame.y}%`;
      }
    };

    socketRef.current.onclose = () => {
      console.log('üî¥ WebSocket disconnected');
      setConnected(false);
    };

    return () => {
      socketRef.current.close();
    };
  }, []);

  const handleStart = () => {
    if (connected) socketRef.current.send('start');
  };

  const handleStop = () => {
    if (connected) socketRef.current.send('stop');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üöÄ WebSocket Animation</h1>
      <button onClick={handleStart}>Start</button>
      <button onClick={handleStop}>Stop</button>

      <div
        style={{
          position: 'relative',
          width: '100%',
          height: '400px',
          border: '2px solid black',
          marginTop: '2rem',
          overflow: 'hidden',
          backgroundColor: '#f0f0f0',
        }}
      >
        <div
          ref={dotRef}
          style={{
            position: 'absolute',
            width: '30px',
            height: '30px',
            backgroundColor: 'red',
            borderRadius: '50%',
            transition: 'top 0.1s linear, left 0.1s linear',
          }}
        ></div>
      </div>

      {data && (
        <div style={{ marginTop: '1rem' }}>
          <p>üïí {new Date(data.timestamp).toLocaleTimeString()}</p>
          <p>üìç X: {data.x.toFixed(2)}, Y: {data.y.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default App;
