function App() {
  const [animationState, setAnimationState] = useState('idle');
  const [isConnected, setIsConnected] = useState(true);
  const animationTimeoutRef = useRef(null);
  const completionTimeoutRef = useRef(null);

  useEffect(() => {
    return () => {
      if (animationTimeoutRef.current) {
        clearTimeout(animationTimeoutRef.current);
      }
      if (completionTimeoutRef.current) {
        clearTimeout(completionTimeoutRef.current);
      }
    };
  }, []);

  const handleStartAnimation = () => {
    // Clear any existing timeouts
    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
    }
    if (completionTimeoutRef.current) {
      clearTimeout(completionTimeoutRef.current);
    }

    // Always start from idle state
    setAnimationState('idle');

    // Small delay to ensure state reset, then start expanding
    setTimeout(() => {
      setAnimationState('expanding');

      // After expansion completes, start shrinking
      animationTimeoutRef.current = setTimeout(() => {
        setAnimationState('shrinking');

        // After shrinking completes, mark as completed
        setTimeout(() => {
          setAnimationState('completed');

          // After completion animation, reset to idle
          completionTimeoutRef.current = setTimeout(() => {
            setAnimationState('idle');
          }, 1200); // Duration of completion fade out
        }, 1000); // Duration of shrinking animation
      }, 2000); // Duration of expanding animation
    }, 50); // Small delay to ensure state reset
  };

  const handleStopAnimation = () => {
    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
    }
    if (completionTimeoutRef.current) {
      clearTimeout(completionTimeoutRef.current);
    }

    setAnimationState('idle');
  };

  return (
    <>
      <style>{styles}</style>
      <div className="app">
        <div className="animation-container">
          <h2 className="animation-text">
            Apple Intelligence
          </h2>

          <div className="gradient-bar-container">
            <div className={`gradient-bar ${animationState}`}></div>
          </div>
        </div>

        <div className="controls">
          <button 
            onClick={handleStartAnimation}
            disabled={!isConnected || (animationState === 'expanding' || animationState === 'shrinking')}
            className="start-button"
          >
            Start Animation
          </button>
          <button 
            onClick={handleStopAnimation}
            disabled={!isConnected || animationState === 'idle'}
            className="stop-button"
          >
            Stop Animation
          </button>
        </div>

        <p className="connection-status">
          Connection Status: {isConnected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
        </p>
      </div>
    </>
  );
}

export default App;
