// App.js
import React, { useRef, useState, useEffect } from 'react';

const App = () => {
  const [data, setData] = useState(null);
  const socketRef = useRef(null);
  const [connected, setConnected] = useState(false);
  const gradientBarRef = useRef(null);

  useEffect(() => {
    socketRef.current = new WebSocket('ws://localhost:8080');

    socketRef.current.onopen = () => {
      console.log('‚úÖ Connected to WebSocket server');
      setConnected(true);
    };

    socketRef.current.onmessage = (event) => {
      const frame = JSON.parse(event.data);
      setData(frame);

      if (gradientBarRef.current) {
        gradientBarRef.current.style.left = `${frame.x}%`;
        gradientBarRef.current.style.top = `${frame.y}%`;
      }
    };

    socketRef.current.onclose = () => {
      console.log('üî¥ WebSocket disconnected');
      setConnected(false);
    };

    return () => {
      socketRef.current.close();
    };
  }, []);

  const handleStart = () => {
    if (connected) socketRef.current.send('start');
  };

  const handleStop = () => {
    if (connected) socketRef.current.send('stop');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>‚ú® WebSocket Gradient Animation</h1>
      <button onClick={handleStart} style={{ marginRight: '10px' }}>
        Start
      </button>
      <button onClick={handleStop}>Stop</button>

      <div
        style={{
          position: 'relative',
          width: '100%',
          height: '400px',
          border: '2px solid black',
          marginTop: '2rem',
          overflow: 'hidden',
          backgroundColor: '#ffffff',
        }}
      >
        <div
          ref={gradientBarRef}
          style={{
            position: 'absolute',
            width: '200px',
            height: '20px',
            background: 'linear-gradient(to right, #0af, #a0f, #f06, #f90)',
            borderRadius: '10px',
            filter: 'blur(20px)',
            transition: 'top 0.1s linear, left 0.1s linear',
          }}
        ></div>
      </div>

      {data && (
        <div style={{ marginTop: '1rem' }}>
          <p>üïí {new Date(data.timestamp).toLocaleTimeString()}</p>
          <p>üìç X: {data.x.toFixed(2)}%, Y: {data.y.toFixed(2)}%</p>
        </div>
      )}
    </div>
  );
};

export default App;
