import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function App() {
  const [animationState, setAnimationState] = useState("idle");
  const [showTextGradient, setShowTextGradient] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [progress, setProgress] = useState(0); // <-- new

  const wsRef = useRef(null); // WebSocket ref
  const animationTimeoutRef = useRef(null);
  const shrinkingTimeoutRef = useRef(null);
  const textGradientTimeoutRef = useRef(null);

  // CONNECT WEBSOCKET
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8080");
    wsRef.current = ws;

    ws.onopen = () => {
      setIsConnected(true);
      console.log("WebSocket connected");
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.progress !== undefined) {
          setProgress(data.progress); // update progress state
        }
      } catch (err) {
        console.error("Invalid message from server:", event.data);
      }
    };

    ws.onclose = () => {
      setIsConnected(false);
      console.log("WebSocket disconnected");
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
      ws.close();
    };

    return () => {
      ws.close();
    };
  }, []);

  const clearAllTimeouts = () => {
    clearTimeout(animationTimeoutRef.current);
    clearTimeout(shrinkingTimeoutRef.current);
    clearTimeout(textGradientTimeoutRef.current);
  };
const handleStartAnimation = () => {
  clearAllTimeouts();
  setAnimationState("idle");
  setShowTextGradient(false);
  setProgress(0);

  // Tell server to start sending animation progress
  if (wsRef.current?.readyState === WebSocket.OPEN) {
    wsRef.current.send(JSON.stringify({ type: "START_ANIMATION" }));
  }

  // Immediately go to expanding state
  setTimeout(() => {
    setAnimationState("expanding");

    // Immediately transition to completed (no shrinking or wait)
    animationTimeoutRef.current = setTimeout(() => {
      setAnimationState("completed");
    }, 600); // match the CSS animation duration of .expanding (or slightly more)
  }, 100);
};


  const handleStopAnimation = () => {
    clearAllTimeouts();
    setAnimationState("idle");
    setShowTextGradient(false);
    setProgress(0);

    // Tell server to stop
    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: "STOP_ANIMATION" }));
    }
  };

  useEffect(() => {
    if (animationState === "completed") {
      const delay = setTimeout(() => {
        setShowTextGradient(true);
        setAnimationState("idle");
      }, 300);
      return () => clearTimeout(delay);
    }
  }, [animationState]);

  const isAnimating =
    animationState === "expanding" ||
    animationState === "shrinking" ||
    animationState === "completed";

  return (
    <div className="app">
      <div className="animation-container">
        <div className={`gradient-bar-container ${animationState}`}>
          <div
            className={`gradient-bar ${animationState}`}
            onAnimationEnd={() => {}}
          />
        </div>

        <h2
          className={`text-title ${isAnimating ? "lift" : ""} ${
            showTextGradient ? "gradient-active" : ""
          }`}
        >
          Apple Intelligence
        </h2>
        {/* Display progress from WebSocket */}
        <p style={{ fontSize: "1rem", marginTop: "1rem", color: "#666" }}>
          Server Progress: {progress}%
        </p>
      </div>

      <div className="button-container">
        <button
          onClick={handleStartAnimation}
          disabled={!isConnected || isAnimating}
          className="start-button"
        >
          Start Animation
        </button>

        <button
          onClick={handleStopAnimation}
          disabled={!isConnected || animationState === "idle"}
          className="stop-button"
        >
          Stop Animation
        </button>
      </div>

      <p className="connection-status">
        Connection Status: {isConnected ? "Connected" : " Disconnected"}
      </p>
<div className="self-section">      <p className="self">Made by Preet Gusain</p>
      <p><a href="https://github.com/preetcoder12/Web3Task" target="__blank">github</a></p></div>
    </div>
  );
}

export default App;
