import React, { useRef, useState, useEffect } from 'react';

const App = () => {
  const [data, setData] = useState(null);
  const socketRef = useRef(null);
  const [connected, setConnected] = useState(false);

  useEffect(() => {
    socketRef.current = new WebSocket('ws://localhost:8080');

    socketRef.current.onopen = () => {
      console.log('‚úÖ Connected to WebSocket server');
      setConnected(true);
    };

    socketRef.current.onmessage = (event) => {
      const frame = JSON.parse(event.data);
      setData(frame);
    };

    socketRef.current.onclose = () => {
      console.log('üî¥ WebSocket disconnected');
      setConnected(false);
    };

    return () => {
      socketRef.current.close();
    };
  }, []);

  const handleStart = () => {
    if (connected) socketRef.current.send('start');
  };

  const handleStop = () => {
    if (connected) socketRef.current.send('stop');
  };

  return (
    <div style={{ padding: '2rem' }}>
      <h1>üöÄ WebSocket Animation</h1>
      <button onClick={handleStart}>Start</button>
      <button onClick={handleStop}>Stop</button>

      {data && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Animation Frame:</h3>
          <p>üïí {new Date(data.timestamp).toLocaleTimeString()}</p>
          <p>üìç X: {data.x.toFixed(2)}, Y: {data.y.toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

export default App;
