function App() {
  const { isConnected, sendMessage } = useWebSocket(WEBSOCKET_URL);
  const [animationState, setAnimationState] = useState('idle'); // 'idle', 'running', 'completed'
  const animationTimeoutRef = useRef(null);

  useEffect(() => {
    return () => {
      if (animationTimeoutRef.current) {
        clearTimeout(animationTimeoutRef.current);
      }
    };
  }, []);

  const handleStartAnimation = () => {
    if (isConnected) {
      sendMessage({ type: 'START_ANIMATION' });
    }

    setAnimationState('running');

    // Show text after 2 seconds (when bar animation completes)
    animationTimeoutRef.current = setTimeout(() => {
      setAnimationState('completed');
    }, 2000);
  };

  const handleStopAnimation = () => {
    if (isConnected) {
      sendMessage({ type: 'STOP_ANIMATION' });
    }

    if (animationTimeoutRef.current) {
      clearTimeout(animationTimeoutRef.current);
    }

    setAnimationState('idle');
  };

  return (
    <div style={{
      fontFamily: 'system-ui, -apple-system, sans-serif',
      textAlign: 'center',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      backgroundColor: '#fff',
      overflow: 'hidden',
      position: 'relative',
      margin: 0,
      padding: 0
    }}>
      <div style={{
        position: 'relative',
        width: '100%',
        maxWidth: '1200px',
        height: '200px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        overflow: 'visible'
      }}>
        {/* Gradient Bar */}
        <div style={{
          position: 'relative',
          width: '100%',
          height: '4px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center'
        }}>
          <div style={{
            height: '4px',
            background: 'linear-gradient(to right, #4A9EFF 0%, #A855F7 25%, #EC4899 50%, #F59E0B 75%, #EF4444 100%)',
            borderRadius: '2px',
            transformOrigin: 'center',
            transform: animationState === 'idle' ? 'scaleX(0)' : 'scaleX(1)',
            width: animationState === 'idle' ? '0%' : '80%',
            transition: animationState === 'idle' ? 'none' : 'width 2s cubic-bezier(0.16, 1, 0.3, 1), transform 2s cubic-bezier(0.16, 1, 0.3, 1)',
            filter: 'blur(0.5px)',
            boxShadow: animationState !== 'idle' ? '0 0 20px rgba(74, 158, 255, 0.3)' : 'none'
          }} />
        </div>

        {/* Apple Intelligence Text */}
        <h2 style={{
          marginTop: '60px',
          fontSize: '3.5rem',
          fontWeight: '800',
          letterSpacing: '-2px',
          background: 'linear-gradient(to right, #4A9EFF 0%, #A855F7 25%, #EC4899 50%, #F59E0B 75%, #EF4444 100%)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          backgroundClip: 'text',
          opacity: animationState === 'completed' ? 1 : 0,
          transform: animationState === 'completed' ? 'translateY(0)' : 'translateY(20px)',
          transition: 'opacity 0.8s cubic-bezier(0.16, 1, 0.3, 1), transform 0.8s cubic-bezier(0.16, 1, 0.3, 1)',
          margin: 0,
          lineHeight: 1
        }}>
          Apple Intelligence
        </h2>
      </div>

      {/* Controls */}
      <div style={{ marginTop: '80px' }}>
        <button
          onClick={handleStartAnimation}
          disabled={!isConnected || animationState === 'running'}
          style={{
            margin: '10px',
            padding: '12px 25px',
            fontSize: '18px',
            cursor: !isConnected || animationState === 'running' ? 'not-allowed' : 'pointer',
            border: 'none',
            borderRadius: '12px',
            backgroundColor: !isConnected || animationState === 'running' ? '#cccccc' : '#007AFF',
            color: 'white',
            transition: 'all 0.2s ease',
            boxShadow: !isConnected || animationState === 'running' ? 'none' : '0 4px 12px rgba(0, 122, 255, 0.3)',
            fontWeight: '600'
          }}
          onMouseOver={(e) => {
            if (!e.target.disabled) {
              e.target.style.backgroundColor = '#0056CC';
              e.target.style.transform = 'translateY(-1px)';
            }
          }}
          onMouseOut={(e) => {
            if (!e.target.disabled) {
              e.target.style.backgroundColor = '#007AFF';
              e.target.style.transform = 'translateY(0)';
            }
          }}
        >
          Start Animation
        </button>

        <button
          onClick={handleStopAnimation}
          disabled={!isConnected || animationState === 'idle'}
          style={{
            margin: '10px',
            padding: '12px 25px',
            fontSize: '18px',
            cursor: !isConnected || animationState === 'idle' ? 'not-allowed' : 'pointer',
            border: 'none',
            borderRadius: '12px',
            backgroundColor: !isConnected || animationState === 'idle' ? '#cccccc' : '#FF3B30',
            color: 'white',
            transition: 'all 0.2s ease',
            boxShadow: !isConnected || animationState === 'idle' ? 'none' : '0 4px 12px rgba(255, 59, 48, 0.3)',
            fontWeight: '600'
          }}
          onMouseOver={(e) => {
            if (!e.target.disabled) {
              e.target.style.backgroundColor = '#D70015';
              e.target.style.transform = 'translateY(-1px)';
            }
          }}
          onMouseOut={(e) => {
            if (!e.target.disabled) {
              e.target.style.backgroundColor = '#FF3B30';
              e.target.style.transform = 'translateY(0)';
            }
          }}
        >
          Stop Animation
        </button>
      </div>

      {/* Connection Status */}
      <p style={{
        position: 'absolute',
        bottom: '20px',
        fontSize: '14px',
        color: '#666',
        margin: 0,
        fontWeight: '500'
      }}>
        Connection Status: {isConnected ? 'ðŸŸ¢ Connected' : 'ðŸ”´ Disconnected'}
      </p>
    </div>
  );
}

export default App;
