import React, { useState, useEffect } from 'react';
import './App.css'; // We'll create this CSS file

function App() {
  const [gradientWidth, setGradientWidth] = useState(0); // Start with 0 width

  useEffect(() => {
    // When the component mounts, set the width to 100% to trigger the animation
    const animationTimeout = setTimeout(() => {
      setGradientWidth(100);
    }, 100); // Small delay to ensure CSS is ready, adjust as needed

    return () => clearTimeout(animationTimeout); // Cleanup the timeout
  }, []); // Empty dependency array means this runs once on mount

  return (
    <div className="App">
      <div
        className="gradient-bar"
        style={{ width: `${gradientWidth}%` }}
      ></div>
      {/* Your other content goes here */}
      <h1 style={{ textAlign: 'center', marginTop: '50px' }}>Apple Intelligence</h1>
    </div>
  );
}

export default App;
