import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function App() {
  const [animationState, setAnimationState] = useState("idle");
  const [isConnected] = useState(true);

  const animationTimeoutRef  = useRef(null);
  const completionTimeoutRef = useRef(null);

  /* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ cleanup on unmount â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  useEffect(() => {
    return () => {
      clearTimeout(animationTimeoutRef.current);
      clearTimeout(completionTimeoutRef.current);
    };
  }, []);

  /* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ handlers â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const handleStartAnimation = () => {
    clearTimeout(animationTimeoutRef.current);
    clearTimeout(completionTimeoutRef.current);

    setAnimationState("idle");

    setTimeout(() => {
      setAnimationState("expanding");

      animationTimeoutRef.current = setTimeout(() => {
        setAnimationState("shrinking");

        setTimeout(() => {
          setAnimationState("completed");

          completionTimeoutRef.current = setTimeout(() => {
            setAnimationState("idle");
          }, 1200);
        }, 1000);
      }, 2000);
    }, 50);
  };

  const handleStopAnimation = () => {
    clearTimeout(animationTimeoutRef.current);
    clearTimeout(completionTimeoutRef.current);
    setAnimationState("idle");
  };

  /* Â­â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ derived flags â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  const isAnimating =
    animationState === "expanding" || animationState === "shrinking";

  /* â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ UI â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
  return (
    <div className="app">
      <div className="animation-container">
        <div className={`gradient-bar-container ${animationState}`}>
          <div className={`gradient-bar ${animationState}`} />
        </div>

        <h2 className={`text-title ${isAnimating ? "lift" : ""}`}>
          Apple Intelligence
        </h2>
      </div>

      <div className="button-container">
        <button
          onClick={handleStartAnimation}
          disabled={!isConnected || isAnimating}
          className="start-button"
        >
          Start Animation
        </button>

        <button
          onClick={handleStopAnimation}
          disabled={!isConnected || animationState === "idle"}
          className="stop-button"
        >
          Stop Animation
        </button>
      </div>

      <p className="connection-status">
        Connection Status: {isConnected ? "ðŸŸ¢ Connected" : "ðŸ”´ Disconnected"}
      </p>
    </div>
  );
}

export default App;
