import React, { useState, useEffect, useRef } from "react";
import "./App.css";

function App() {
  const [animationState, setAnimationState] = useState("idle");
  const [showTextGradient, setShowTextGradient] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [progress, setProgress] = useState(0);

  const wsRef = useRef(null);
  const animationTimeoutRef = useRef(null);
  const shrinkingTimeoutRef = useRef(null);
  const textGradientTimeoutRef = useRef(null);

  // WebSocket connection setup
  useEffect(() => {
    const ws = new WebSocket("ws://localhost:8080");
    wsRef.current = ws;

    ws.onopen = () => {
      setIsConnected(true);
      console.log("WebSocket connected");
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.progress !== undefined) {
          setProgress(data.progress);
        }
      } catch (err) {
        console.error("Invalid message from server:", event.data);
      }
    };

    ws.onclose = () => {
      setIsConnected(false);
      console.log("WebSocket disconnected");
    };

    ws.onerror = (error) => {
      console.error("WebSocket error:", error);
      ws.close();
    };

    return () => {
      ws.close();
    };
  }, []);

  const clearAllTimeouts = () => {
    clearTimeout(animationTimeoutRef.current);
    clearTimeout(shrinkingTimeoutRef.current);
    clearTimeout(textGradientTimeoutRef.current);
  };

  const handleStartAnimation = () => {
    clearAllTimeouts();
    setAnimationState("idle");
    setShowTextGradient(false);
    setProgress(0);

    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: "START_ANIMATION" }));
    }

    // Start animation locally
    setTimeout(() => {
      setAnimationState("running");

      animationTimeoutRef.current = setTimeout(() => {
        setAnimationState("idle");
        setShowTextGradient(true);
      }, 3000); // Total animation time (matches keyframe duration)
    }, 100);
  };

  const handleStopAnimation = () => {
    clearAllTimeouts();
    setAnimationState("idle");
    setShowTextGradient(false);
    setProgress(0);

    if (wsRef.current?.readyState === WebSocket.OPEN) {
      wsRef.current.send(JSON.stringify({ type: "STOP_ANIMATION" }));
    }
  };

  return (
    <div className="app">
      <div className="animation-container">
        <div className="gradient-bar-container">
          <div className={`gradient-bar ${animationState === "running" ? "running" : "idle"}`} />
        </div>

        <h2 className={`text-title ${showTextGradient ? "active" : ""}`}>
          Apple Intelligence
        </h2>

        <p style={{ fontSize: "1rem", marginTop: "1rem", color: "#666" }}>
          Server Progress: {progress}%
        </p>
      </div>

      <div className="button-container">
        <button
          onClick={handleStartAnimation}
          disabled={!isConnected || animationState === "running"}
          className="start-button"
        >
          Start Animation
        </button>

        <button
          onClick={handleStopAnimation}
          disabled={!isConnected || animationState === "idle"}
          className="stop-button"
        >
          Stop Animation
        </button>
      </div>

      <p className="connection-status">
        Connection Status: {isConnected ? "Connected" : "Disconnected"}
      </p>

      <div className="self-section">
        <p className="self">Made by Preet Gusain</p>
        <p>
          <a href="https://github.com/preetcoder12/Web3Task" target="__blank">
            github
          </a>
        </p>
      </div>
    </div>
  );
}

export default App;
